package com.sean.designpatterns.command;public class Invoker {	/**	 * 持有命令对象	 */	private Command command = null;		/**	 * 设置调用者持有的命令对象	 */	public void setCommand(Command command) {		this.command = command;	}		/**	 * 要求执行命令的请求	 */	public void runCommand() {		command.execute();	}}