package com.sean.designpatterns.memento;public class Run {	public static void main(String[] args) {		Original original = new Original();		original.setName("A");		original.setId(1);				System.out.println(original.toString());				MementoManager manager = new MementoManager();		Memento mementoA = original.createMemento();		manager.setMemento(mementoA);				original.setName("B");		original.setId(2);				System.out.println(original.toString());				Memento mementoB = manager.getMemento();		original.recoverMemento(mementoB);				System.out.println(original.toString());	}}