package com.sean.designpatterns.observer;public class Run {	public static void main(String[] args) {		ConcreteSubject subject = new ConcreteSubject();				ConcreteOberver oberverA = new ConcreteOberver();		oberverA.setName("A");		ConcreteOberver oberverB = new ConcreteOberver();		oberverB.setName("B");		ConcreteOberver oberverC = new ConcreteOberver();		oberverC.setName("C");				subject.attach(oberverA);		subject.attach(oberverB);		subject.attach(oberverC);		subject.setState("Open");				subject.detach(oberverB);		subject.setState("Closed");	}}