package com.sean.designpatterns.state;public class Monster {		private State state;	private String name;		public Monster() {		this.changeToState(new StatePeaceful(this));	}		public void changeToState(State state) {		this.state = state;		this.state.onEnterState();	}		public void observer() {		this.state.observe();	}		public String getName() {		return name;	}		public void setName(String name) {		this.name = name;	}}