package com.sean.designpatterns.state;public class StateCrazy implements State {		private Monster monster;		public StateCrazy(Monster monster) {		this.monster = monster;	}	@Override	public void onEnterState() {		System.out.println("Monster enter crazy state.");		this.monster.setName("Crazy Monster");	}	@Override	public void observe() {		System.out.println("It's a " + this.monster.getName());	}}