package com.sean.designpatterns.state;public class StatePeaceful implements State {		private Monster monster;		public StatePeaceful(Monster monster) {		this.monster = monster;	}	@Override	public void onEnterState() {		System.out.println("Monster enter peaceful state.");		this.monster.setName("Peaceful Monster");	}	@Override	public void observe() {		System.out.println("It's a " + this.monster.getName());	}}